import React from 'react';
import ReactDOM from 'react-dom';
import Player from './Player'
import Btn from './Btn'
import Dice from './Dice'
import './index.css'

const WINNINGSCORE = 100; 

class Game extends React.Component {
  state = {
    curPlayer : 0,
    totalScore: [0, 0],
    curtScore: [0, 0],
    curPlayer: "player1",
    dice: [0,0],
    winningScore: WINNINGSCORE,
    isWon: false,
    };

    rollDice2 = () => {
      let randomDice1 = Math.floor(6*Math.random())+1;  
      let randomDice2 = Math.floor(6*Math.random())+1;  
      console.log([randomDice1, randomDice2]);
      this.setState({dice: [randomDice1, randomDice2]})
      this.addScoreToPlayer();
    }
    addScoreToPlayer = () => {
      let diceScore = this.state.dice[0] + this.state.dice[1];
      console.log(`diceScore = ${diceScore}`);
      if (this.state.curPlayer === 0)
      this.setState({curtScore: [this.state.curtScore[0] + diceScore, 0]})
    }

  render() {
    return (
      <div className="continuer">
      <Player PlayerName = "Player1" curtScore = {this.state.curtScore[0]} totalScore = {this.state.totalScore[0]}/>
      <div className="game">
      <Btn BtnText ="NEW GAME" />
      <Dice diceArr={this.state.dice}/>
      <Btn BtnText ="ROLL DICE" callback = {this.rollDice2} state = {this.state.dice} />
      <Btn BtnText ="HOLD" />
      <Btn BtnText ="Final Score = 100" />
      </div>
      <Player PlayerName = "Player2" curtScore = {this.state.curtScore[1]} totalScore = {this.state.totalScore[1]}/>
      </div>
    )
  }
}

export default Game;
