import React from 'react';
import ReactDOM from 'react-dom';
import Player from './Player'
import Btn from './Btn'
import './index.css'

const WINNINGSCORE = 100; 

function rollDice(){
  let randomDice1 = Math.floor(6*Math.random())+1;  
  let randomDice2 = Math.floor(6*Math.random())+1;  
  // dice1.src = "dice/" + randomDice + ".jpg";
  console.log([randomDice1, randomDice2]);
  return [randomDice1, randomDice2];
}

class Game extends React.Component {
  state = {
    curPlayer : 1,
    totalScore: { player1: 0, player2: 0 },
    curtScore: 0,
    curPlayer: "player1",
    dice: [0, 0],
    winningScore: WINNINGSCORE,
    isWon: false,
    };
  render() {
    return (
      <div className="continuer">
      <Player PlayerName = "Player1"/>
      <div className="game">
      <Btn BtnText ="NEW GAME" callback = {rollDice}/>
      <Btn BtnText ="ROLL DICE" />
      <Btn BtnText ="HOLD" />
      <Btn BtnText ="Final Score = 100" />
      </div>
      <Player PlayerName = "Player2"/>      
      </div>
    )
  }
}

export default Game;
