import React from 'react';
import ReactDOM from 'react-dom';
import './index.css'


class Player extends React.Component {
  state = {
    play: false,
    curScore : 0,
    totalScore : 0
  }
  render() {
    return (
      <div className="player">
      <div className="playerTitle">{this.props.PlayerName}</div>
      <div className="curScore">{this.state.curScore}</div>
      <div className="totalScore">{this.state.totalScore}</div>
      </div>
    )
  }
}

export default Player;
