import React from 'react';
import ReactDOM from 'react-dom';
import './index.css'


class Player extends React.Component {
  state = {
    curPlayer : 1
  }
  render() {
    return (
      <div className="game">
      <button>NEW GAME</button>
      <button>ROLL DICE</button>
      <button>HOLD</button>
      <button>Final Score = 100 </button>
      </div>
    )
  }
}

export default Player;
